mapper.select:
select c.*,l.id lid,l.name lname from parking_charging c left join parking_lot_information l on c.parkinglotinformationid=l.id


     and c.parkingLotInformationId = #{parkinglotinformationid}
     and c.distinguish = #{distinguish}

mapper.update:
update parking_charging
startingPrice = #{startingprice}, startingPriceDuration = #{startingpriceduration}, increaseIncome = #{increaseincome}, freeTimeState = #{freeTimeState}, startingpriceState = #{startingpriceState}, superiorLimit = #{superiorlimit}, parkingLotInformationId = #{parkinglotinformationid}, type = #{type}, distinguish = #{distinguish}, freetime = #{freetime},
where id = #{id}
