mapper.select:
select p.*,l.id lid,l.name lname from parking_record p left join parking_lot_information l on p.parkinglotinformationid=l.id

     and p.license = #{license}
     and p.parkingLotInformationId = #{parkinglotinformationid}
     and p.admissionTime  = #{admissiontime}
     and p.exitTime  = #{exittime}
     and p.licensePllateColor = #{licensepllatecolor}
     and p.orderNumber = #{ordernumber}
     and p.orderState = #{orderstate}
     and p.payState = #{paystate}
     and  p.money = #{money}
     and p.entranceAndExitName like concat('%', #{entranceandexitname}, '%')
     and p.payTime = #{payTime}
     and p.numberTwo = #{numbertwo}
     and p.numberThree = #{numberthree}
     and p.openid = #{openid}
     and p.indoorPayment = #{indoorPayment}
     and p.parkinglotequipmentid = #{parkinglotequipmentid}
     and p.flee = #{flee}
     and p.parkingeqid = #{parkingeqid}
     and p.supplementaryfee = #{supplementaryfee}
     and p.wxorder = #{wxorder}
     and p.paymentMethod like concat('%',#{paymentmethod},'%')

        and p.admissionTime  >=  CAST(CONCAT(#{starttime},' 00:00:00') AS datetime)


        and p.admissionTime  <=  CAST(CONCAT(#{endtime},' 23:59:59') AS datetime)



order by p.admissionTime desc

select sum(money)from parking_record

     and license = #{license}
     and parkingLotInformationId = #{parkinglotinformationid}
     and admissionTime  = #{admissiontime}
     and exitTime  = #{exittime}
     and licensePllateColor = #{licensepllatecolor}
     and orderNumber = #{ordernumber}
     and orderState = #{orderstate}
     and orderState = #{orderstate}
     and  parkingeqid = #{parkingeqid}
     and entranceAndExitName like concat('%', #{entranceandexitname}, '%')

     and numberTwo = #{numbertwo}
     and numberThree = #{numberthree}
     and openid = #{openid}
     and indoorPayment = #{indoorPayment}
     and parkinglotequipmentid = #{parkinglotequipmentid}
     and flee = #{flee}
     and supplementaryfee = #{supplementaryfee}
     and wxorder = #{wxorder}
     and paymentMethod like concat('%',#{paymentmethod},'%')

        and admissionTime  >=  CAST(CONCAT(#{starttime},' 00:00:00') AS datetime)


        and admissionTime  <=  CAST(CONCAT(#{endtime},' 23:59:59') AS datetime)

    and payState=1

mapper.update:
update parking_record
license = #{license}, parkingLotInformationId = #{parkinglotinformationid}, admissionTime = #{admissiontime}, exitTime = #{exittime}, licensePllateColor = #{licensepllatecolor}, orderNumber = #{ordernumber}, orderState = #{orderstate}, payState = #{paystate}, money = #{money}, entranceAndExitName = #{entranceandexitname}, payTime = #{payTime}, numberTwo = #{numbertwo}, numberThree = #{numberthree}, discountAmount = #{discountamount}, amountPayable = #{amountpayable}, paymentMethod = #{paymentmethod}, openid = #{openid}, indoorPayment = #{indoorPayment}, parkinglotequipmentid = #{parkinglotequipmentid}, flee = #{flee}, supplementaryfee = #{supplementaryfee}, wxorder = #{wxorder}, parkingeqid = #{parkingeqid},
where id = #{id}
